export const getSignalIcon = (signalStrength: number, size: number = 50) => {
  const iconStyle = {
    width: `${size}px`,
    height: `${size}px`,
  };

  if (signalStrength >= -49) {
    return (
      <img
        src="data:image/png;base64,"
        alt="Excellent Signal"
        style={iconStyle}
      />
    );
  } else if (signalStrength >= -59) {
    return (
      <img
        src="data:image/png;base64,"
        alt="Good Signal"
        style={iconStyle}
      />
    );
  } else if (signalStrength >= -69) {
    return (
      <img
        src="data:image/png;base64,"
        alt="Fair Signal"
        style={iconStyle}
      />
    );
  } else {
    return (
      <img
        src="data:image/png;base64,"
        alt="Weak Signal"
        style={iconStyle}
      />
    );
  }
};  